def icon(name: str, size: int = 20, color: str = 'currentColor') -> str:
    """返回格式化的SVG图标字符串"""
    if name not in ICONS:
        return f'<!-- icon "{name}" not found -->'
    return ICONS[name].format(size=size, color=color)
